    expected_dtypes = {
        'period_year': np.dtype('int64'),
        'period_month': np.dtype('int64'),
        'revenue_ttm': np.dtype('float64'),
        'gross_profit_ttm': np.dtype('float64'),
        'ebitda_ttm': np.dtype('float64'),
        'revenue_q': np.dtype('float64'),
        'revenue_c': np.dtype('float64'),
        'cash_and_cash_equivalents': np.dtype('float64'),
        'equity': np.dtype('float64'),
        'company_id': np.dtype('int64')
    }
    
    # Her sütunun beklenen veri tipinde olduğunu doğrula
    for col, expected_dtype in expected_dtypes.items():
        assert result_df[col].dtype == expected_dtype, f"Sütun '{col}' için beklenen tip {expected_dtype} ama gelen tip {result_df[col].dtype}"

    # Q hesaplarını doğrula
    bimas_2024_q1_revenue_c = result_df[(result_df["company_id"]==1) & (result_df["period_month"]==3)]["revenue_c"]
        
        
